export function createControl(config, validation) {
    return {
        ...config,
        validation,
        valid: !validation,
        touched: false,
        value: ''
    }
}

export function validate(value, valitation = null) {
    if (!valitation) {
        return true
    }

    let isValid = true

    if (valitation.required) {
        isValid = value.trim() !== '' && isValid
    }

}
