import React, {Component} from 'react'
import classes from './Drawer.module.css'

const links = ['1', '2', '3']

class Drawer extends Component {

    renderLinks() {
        return links.map( (link, index) => {
            return (
                <li key={index}>
                    <a>Link {link}</a>
                </li>
            )
        })
    }

    render() {

        const cls = [
            classes.Drawer
        ]

        if (true) {
            cls.push('classes.close')
        }

        return (
            <nav className={cls.join(' ')}>
                <ul>
                   {this.renderLinks()}
                </ul>
            </nav>
        )
    }
}

export default Drawer
