import React, {Component} from 'react'
import classes from './QuizCreator.module.css'
import Button from "../../components/UI/Button/Button";
import {createControl} from '../../form/formFramework'
import Input from "../../components/UI/Input/Input";

// Сокращение дублирование кода функции createControl() для state.formControls.option
function createOptionControl(number) {
    return createControl({
        label: `Вариант ${number}`,
        errorMessage: 'Значение не может быть пустым',
        id: number
    }, {required: true})
}

function createFormControls() {
    return {
        question: createControl({
            label: 'Введите вопрос',
            errorMessage: 'Вопрос не может быть пустым'
        }, {required: true}),
        option1: createOptionControl(1),
        option2: createOptionControl(2),
        option3: createOptionControl(3),
        option4: createOptionControl(4)
    }
}

export default class QuizCreator extends Component {

    state = {
        quiz: [],
        formControls: createFormControls()
    }

    submitHandler = (event) => event.preventDefault()
    addQuestionHandler = () => {
    }
    createQuizHandler = () => {
    }

    changeHandler = () => {

    }

    renderControls() {
        return Object.keys(this.state.formControls).map((controlName, index) => {
            const control = this.state.formControls[controlName]
            return (
                <Input
                    label={control.label}
                    value={control.value}
                    valid={control.valid}
                    shouldValidate={!!control.validation}
                    touched={control.touched}
                    errorMessage={control.errorMessage}
                    onChange={event => this.changeHandler(event.target.value, controlName)}
                />
            )
        })
    }

    render() {
        return (
            <div className={classes.QuizCreator}>
                <div>
                    <h1>QuizCreator</h1>
                    <form onSubmit={this.submitHandler}>

                        {this.renderControls()}

                        <select>123</select>

                        <Button type='primary' onClick={this.addQuestionHandler}>Добавить вопрос</Button>
                        <Button type='success' onClick={this.addQuestionHandler}>Добавить вопрос</Button>

                    </form>
                </div>
            </div>
        )
    }
}
