import React from 'react'
import classes from './CounterTwo.module.css'
import {connect} from 'react-redux'

const CounterTwo = props => {
    console.log(props)
    return (
        <div className={classes.CounterTwo}>
            <h1>Счетчик <strong>{props.counter}</strong></h1>
            <hr/>
            <div className={classes.Action}>
                <button onClick={props.onAdd}>Добавить 1</button>
                <button onClick={props.onSub}>Отнять 1</button>
                <button onClick={() => props.onAddNumber(10)}>Добавить 10</button>
                <button onClick={() => props.onSubNumber(10)}>Отнять 10</button>
            </div>
        </div>
    )
}

function mapStateToProps(state) {
    return {
        counter: state
    }
}

function mapDispatchToProps(dispatch) {
    return {
        onAdd: () => dispatch({type: 'ADD'}),
        onSub: () => dispatch({type: 'SUB'}),
        onAddNumber: number => dispatch({type: 'ADD_NUMBER', payload: number}),
        onSubNumber: number => dispatch({type: 'SUB_NUMBER', payload: number})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CounterTwo)
