import React from 'react'
import classes from './CounterOne.module.css'
import {connect} from 'react-redux'

const CounterOne = () => {
    return (
        <div className={classes.CounterOne}>
            <h1>Счетчик <strong>{/*{this.props.counter}*/}</strong></h1>
            <hr/>
            <div className={classes.Action}>
                <button onClick={this.props.onAdd}>Добавить 1</button>
                <button onClick={this.props.onSub}>Отнять 1</button>
                <button onClick={() => this.props.onAddNumber(10)}>Добавить 10</button>
                <button onClick={() => this.props.onSubNumber(10)}>Отнять 10</button>
            </div>
        </div>
    )
}

function mapStateToProps(state) {
    return {
        counter: state.counter
    }
}

function mapDispatchToProps(dispatch) {
    return {
        onAdd: () => dispatch({type: 'ADD'}),
        onSub: () => dispatch({type: 'SUB'}),
        onAddNumber: number => dispatch({type: 'ADD_NUMBER', payload: number}),
        onSubNumber: number => dispatch({type: 'SUB_NUMBER', payload: number})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(CounterOne)
