const initialState = {
    counter2: 0
}

export default function counter2(state = initialState, action) {

    switch (action.type) {
        case 'ADD_2':
            return {
                counter2: state.counter2 + 1
            }
        case 'SUB_2' :
            return {
                counter2: state.counter2 - 1
            }
        case 'ADD_NUMBER_2' :
            return {
                counter2: state.counter2 + action.payload
            }
        case 'SUB_NUMBER_2' :
            return {
                counter2: state.counter2 - action.payload
            }
        default: return state
    }
}
