import React, {Component} from "react";
import './App.css';
import Car from "./Car/Car";

export default class App extends Component {

    state = {
        car: [
            {name: 'Ford', year: 2018},
            {name: 'Audi', year: 2016},
            {name: 'Mazda', year: 2010}
        ],
        pageTitle: 'React components',
        showCars: false
    }

    toggleCarsHandler = () => {
        this.setState({showCars: !this.state.showCars})
    }

    changeTitleHandler = (newTitle) => {
        this.setState({pageTitle: newTitle})
    }

    handlerInput = (event) => {
        this.setState({pageTitle: event.target.value})
    }

    onChangeName = (event) => {
        const car = event.targ
        console.log(event)
    }

    render() {

        const divStyle = {
            textAlign: 'center'
        }
        let cars = null
        if (this.state.showCars) {

            cars = this.state.car.map((car, index) => {
                return (
                    <Car
                        key={index}
                        name={car.name}
                        year={car.year}
                        changeTitleHandler={this.changeTitleHandler.bind(this, car.name)}
                        onChangeName={this.onChangeName.bind(this, index)}
                    />
                )
            })
        }

        return (
            <div className="App" style={divStyle}>
                <h1>{this.state.pageTitle}</h1>
                <button onClick={() => {
                    this.toggleCarsHandler()
                }}>Toggle cars
                </button>
                { cars }
            </div>
        )
    }
}
