import React, {Component} from "react";
import './App.css';
import Car from "./Car/Car";

export default class App extends Component {

    divStyle = {
        textAlign: 'center'
    }

    state = {
        car: [
            {name: 'Ford', year: 2018},
            {name: 'Audi', year: 2016},
            {name: 'Mazda', year: 2010}
        ],
        pageTitle: 'React components'
    }

    changeTitleHandler = (newTitle) => {
        this.setState({pageTitle : newTitle})
    }

    render() {
        return (
            <div className="App" style={this.divStyle}>
                <h1>{this.state.pageTitle}</h1>
                <button onClick={this.changeTitleHandler.bind(this, 'Changed!')}>Change title</button>
                <Car
                    name={this.state.car[0].name}
                    year={this.state.car[0].year}
                    changeTitleHandler={this.changeTitleHandler.bind(this, this.state.car[0].name)}
                />
                <Car
                    name={this.state.car[1].name}
                    year={this.state.car[1].year}
                    changeTitleHandler={() => {this.changeTitleHandler(this.state.car[1].name)}}
                />
                <Car
                    name={this.state.car[2].name}
                    year={this.state.car[2].year}
                    changeTitleHandler={this.changeTitleHandler}
                />
            </div>
        )
    }
}
