import React, {Component} from "react";
import './App.css';
import Car from "./Car/Car";

export default class App extends Component {

    divStyle = {
        textAlign: 'center'
    }

    state = {
        car: [
            {name: 'Ford', year: 2018},
            {name: 'Audi', year: 2016},
            {name: 'Mazda', year: 2010}
        ],
        pageTitle: 'React components',
        showCars: false
    }

    toggleCarsHandler = () => {
        this.setState({showCars: !this.state.showCars})
        console.log(this.state.showCars)
    }

    changeTitleHandler = (newTitle) => {
        this.setState({pageTitle: newTitle})
    }

    handlerInput = (event) => {
        this.setState({pageTitle: event.target.value})
    }

    render() {
        return (
            <div className="App" style={this.divStyle}>
                <h1>{this.state.pageTitle}</h1>
                <button onClick={() => {
                    this.toggleCarsHandler()
                }}>Toggle cars
                </button>

                {
                    this.state.showCars
                        ? this.state.car.map((car, index) => {
                        return (
                            <Car
                                key={index}
                                name={car.name}
                                year={car.year}
                                changeTitleHandler={this.changeTitleHandler.bind(this, car.name)}
                            />
                        )
                    }) : console.log(this.state.showCars+'2')
                }

                {
                    this.changeToggolBtn
                        ? this.state.car.map((car, index) => {
                            return (
                                <Car
                                    key={index}
                                    name={car.name}
                                    year={car.year}
                                    changeTitleHandler={this.changeTitleHandler.bind(this, car.name)}
                                />
                            )
                        })
                        : null
                }

            </div>
        )
    }
}
