import React, {Component} from "react";
import './App.css';
import Car from "./Car/Car";

export default class App extends Component {

    divStyle = {
        textAlign: 'center'
    }

    state = {
        car: [
            {name: 'Ford', year: 2018},
            {name: 'Audi', year: 2016},
            {name: 'Mazda', year: 2010}
        ],
        pageTitle: 'React components',
        toggleBtn: false
    }

    changeToggolBtn = () => {
        this.setState({toggleBtn: !this.state.toggleBtn})
    }

    changeTitleHandler = (newTitle) => {
        this.setState({pageTitle: newTitle})
    }

    handlerInput = (event) => {
        this.setState({pageTitle: event.target.value})
    }

    render() {
        return (
            <div className="App" style={this.divStyle}>
                <h1>{this.state.pageTitle}</h1>
                <input type='text' onChange={this.handlerInput}/>
                <button onClick={this.changeTitleHandler.bind(this, 'Changed!')}>Change title</button>
                {this.state.car.map((car, index) => {
                    return (
                        <Car
                            key={index}
                            name={car.name}
                            year={car.year}
                            changeTitleHandler={this.changeTitleHandler.bind(this, car.name)}
                        />
                    )
                })}

            </div>
        )
    }
}
