import React from "react";
import './App.css';
import Car from "./Car/Car";

export default class App extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            cars: [
                {name: 'Ford', year: 2018},
                {name: 'Audi', year: 2016},
                {name: 'Mazda', year: 2010}
            ],
            pageTitle: 'React components',
            showCars: false
        }
    }

    toggleCarsHandler = () => {
        this.setState({showCars: !this.state.showCars})
    }

    handlerInput = (event) => {
        this.setState({pageTitle: event.target.value})
    }

    onChangeName = (name, index) => {
        const car = this.state.cars[index]
        car.name = name
        const cars = [...this.state.cars]
        this.setState({cars})
    }

    onDelete = (index) => {
        const cars = this.state.cars.concat()
        cars.splice(index, 1)
        this.setState({cars})
    }

    contentWillMount (){
        console.log('App run contentWillMount')
    }

    contentDidMount (){
        console.log('App run contentDidMount')
    }

    render() {

        const divStyle = {
            textAlign: 'center'
        }
        let cars = null
        if (this.state.showCars) {

            cars = this.state.cars.map((car, index) => {
                return (
                    <Car
                        key={index}
                        name={car.name}
                        year={car.year}
                        onChangeName={(event) => {
                            this.onChangeName(event.target.value, index)
                        }}
                        onDelete={(event) => {
                            this.onDelete(index)
                        }}
                    />
                )
            })
        }

        return (
            <div className="App" style={divStyle}>
                <h1>{this.state.pageTitle}</h1>
                <button onClick={() => {
                    this.toggleCarsHandler()
                }}>Toggle cars
                </button>
                <div style={{
                    width: '400px',
                    margin: 'auto',
                    paddingTop: '20px'
                }}>
                    {cars}
                </div>
            </div>
        )
    }
}
